    function delete_directory($dir)
    {
        if (is_dir($dir))
        {
            $dir_handle = opendir($dir);
            if ($dir_handle)
            {
                while ($file = readdir($dir_handle))
                {
                    if ($file != "." && $file != "..")
                    {
                        if (!is_dir($dir . "/" . $file))
                        {
                            unlink($dir . "/" . $file);
                        }
                        else
                        {
                            $this->delete_directory($dir . '/' . $file);
                        }
                    }
                }
                closedir($dir_handle);
            }
            rmdir($dir);
            return true;
        }
        return false;
    }
