                }
                else
                {
                    $balance_update = $loanDetails->amount + $ssbAccountDetails->balance;
                }
                $ssbBalance = SavingAccount::find($ssbAccountDetails->id);
                $ssbBalance->balance = $balance_update;
                $ssbBalance->save();
