            else
            {
                $groupId = Grouploans::where('account_number', $request->account_number)
                    ->first();
                if ($groupId)
                {
                    $record = LoanDayBooks::where('loan_type', 3)->where('loan_sub_type', 0)
                        ->where('is_deleted', 0) /*->where('payment_mode',0)*/
                        ->where('loan_id', $groupId->id)
                        ->orderBy('id', 'desc')
                        ->get();
                    if (count($record) > 0)
                    {
                        $status = 1;
